import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      items : [],
      size : 0
    };

  };

  componentDidMount(){

    var myHeaders = new Headers();
    var formdata = new FormData();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");

    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch("https://api.imgur.com/3/album/hvsj8LF/images", requestOptions)
      .then(response => response.json())
      .then(res => {this.setState({ size : res.data.length , items : res.data}), res.forEach(
        (item) => items.push(item)
       );})
      .catch(error => console.log(error));

  }

  render(){
    var isLoaded = true;
    //var a = this.state._link;
    if (!isLoaded){
      return <div>Loading...</div>;
    }
    //<img src={this.state._link}/>
    else {
    return (
      <div className='App'>
        <div>
         {this.state.items}{this.state.size}
        </div>
      </div>
      );
    }
  }
}

export default App;
