import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      _link : null
    };

  };

  componentDidMount(){

    var myHeaders = new Headers();
    var formdata = new FormData();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");

    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch("https://api.imgur.com/3/image/4HWroDT", requestOptions)
      .then(response => response.json())
      .then(res => {this.setState({_link : res.data.link})})
      .catch(error => console.log(error));
      //console.log(this.state); 
  }

  render(){
    var isLoaded = true;
    //var a = this.state._link;
    if (!isLoaded){
      return <div>Loading...</div>;
    }
    else {
    return (
      <div className='App'>
        <div>
          <img src={this.state._link}/>
        </div>
      </div>
      );
    }
  }
}

export default App;
