import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      items : {},
      size : 0,
      currentImage : 0,
      imgLinks : []
    };

  };

  componentWillUnmount() {
    if (this.interval) {
      clearInterval(this.interval);
    }
  }

  componentDidMount(){
    this.interval = setInterval(() => this.changeBackgroundImage(), 1000);
    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch("https://api.imgur.com/3/album/hvsj8LF/images", requestOptions)
      .then(response => response.json())
      .then((result) => {
        let tmp = []
        for (var i = 0; i < result.data.length; i++) {
            tmp.push(result.data[i].link)
          }
        this.setState({
            imgLinks: tmp});
          })
      .catch(error => console.log(error));


  }

  changeBackgroundImage() {
    let newCurrentImg = 0;
    const images = this.state.imgLinks;
    const currentImg = this.state.currentImg
    const noOfImages = images.length;

    if (currentImg !== noOfImages - 1) {
      newCurrentImg = currentImg + 1;
    }
    console.log(newCurrentImg);
    this.setState({currentImg: newCurrentImg});
  }

  render(){
    console.log(this.state.imgLinks);
    console.log(this.state.currentImage);

    return (
      <div className='App'>
      <img src={this.state.imgLinks[this.state.currentImage]} />
      </div>
      );

  }
}

export default App;
