import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      items : {},
      size : 0,
      currentImage : 0,
      imgLinks : [],
      imgInformations : [],
      pressed : false, 
      timer : 0
    };

  };

  updateCountDown(){
      if (!this.state.pressed){
        const tmp = this.state.timer - 1;
        this.setState({timer : tmp});
        if (this.state.timer == -1){
          this.setState({timer : 10})
          this.changeBackgroundImage();
        }
    }


  }

  componentWillUnmount() {
    if (this.interval) {
      //clearInterval(this.interval);
    }
  }

  componentDidMount(){

    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch("https://api.imgur.com/3/album/hvsj8LF/images", requestOptions)
      .then(response => response.json())
      .then((result) => {
        let tmpLink = [];
        let tmpInfo = [];
        for (var i = 0; i < result.data.length; i++) {
            tmpLink.push(result.data[i].link);
            tmpInfo.push(`Id : ${result.data[i].id} | 
                          Title : ${result.data[i].title} |
                          Description : ${result.data[i].description} | 
                          Type : ${result.data[i].type} | 
                          Views : ${result.data[i].views}`)


          }
        this.setState({
            imgLinks: tmpLink,
            imgInformations: tmpInfo});
          })
      .catch(error => console.log(error));



    this.interval = setInterval(() => this.updateCountDown(), 1000);

  }

  changeBackgroundImage() {
    if (!this.state.pressed){
      let newCurrentImg = 0;
      const images = this.state.imgLinks;
      const currentImg = this.state.currentImage
      const noOfImages = images.length;

      if (currentImg !== noOfImages - 1) {
        newCurrentImg = currentImg + 1;
      }
      this.setState({currentImage: newCurrentImg});
      this.updateCountDown();
      console.log(this.state.timer);

    }
  }

  handleImageClick = () => {
    this.setState({pressed : !this.state.pressed});
  }

  render(){    
    return (
      <div className='App'>
      <div>Timer : 00:0{this.state.timer} Seconds</div>
      <button><img src={this.state.imgLinks[this.state.currentImage]} onClick={this.handleImageClick}/></button>
      <div>

        <b>{self.state.pressed ? this.state.imgInformations[this.state.currentImage] : 'Click on the Image to show More informations'}</b> logged in.
      </div>
      </div>
      );
  }
}

export default App;
