import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'


class App extends React.Component {
  constructor(props) {
    super(props);

    const images = [
      "https://wavegarden.com/wp-content/uploads/2019/11/wavegarden-cove-Melburne-01.jpg",
      "https://cdn.radiofrance.fr/s3/cruiser-production/2021/08/696b8bea-3893-4a18-b549-515a4c641d93/838_gettyimages-982747408.jpg",
      "https://upload.wikimedia.org/wikipedia/commons/4/4d/Cat_March_2010-1.jpg"
    ];

    this.state = {
      images,
      currentImg: 0
    }
  }

  componentDidMount() {
    this.interval = setInterval(() => this.changeBackgroundImage(), 1000);
  }

  componentWillUnmount() {
    if (this.interval) {
      clearInterval(this.interval);
    }
  }

  changeBackgroundImage() {
    let newCurrentImg = 0;
    const {images, currentImg} = this.state;
    const noOfImages = images.length;

    if (currentImg !== noOfImages - 1) {
      newCurrentImg = currentImg + 1;
    }

    this.setState({currentImg: newCurrentImg});
  }

  render() {
    const {images, currentImg} = this.state;
    const urlString = `url('${images[currentImg]}')`;

    return (
      <div className="App">
        <div className='dynamicImage' style={{backgroundImage: urlString}} >
        </div>
      </div >
    );
  }
}

export default App
