import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      items : {},
      size : 0,
      currentImage : 0
    };

  };

  componentDidMount(){

    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");

    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };
    fetch("https://api.imgur.com/3/album/hvsj8LF/images", requestOptions)
      .then(response => response.json())
      .then(result => {this.setState({ size : result.data.length , items : result.data});})
      .catch(error => console.log(error));

  }

  render(){
    const imgs = this.state.items;
    let v = imgs[0];
    console.log(imgs);
