import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      items : {},
      size : 0,
      currentImage : 0,
      imgLinks : [],
      imgInformations : [],
      pause : false, 
      isDataLoaded : true,
      timer : 0,
      AlbumHash : "hvsj8LF",
      userInput : ""
    };

  };

  updateCountDown(){
      if (!this.state.pause && this.state.isDataLoaded){
        const tmp = this.state.timer - 1;
        this.setState({timer : tmp});
        if (this.state.timer == -1){
          this.setState({timer : 10})
          this.changeBackgroundImage();
        }
    }
  }

  /*componentWillUnmount() {
    if (this.interval) {
      clearInterval(this.interval);
    }
  }*/

  componentDidMount(){
    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Client-ID 2fe36512ef5b8e3");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch("https://api.imgur.com/3/album/hvsj8LF/images", requestOptions)
      .then(response => response.json())
      .then((result) => {
        let tmpLink = [];
        let tmpInfo = [];
        //result.data.length != 0 ? this.setState({isDataLoaded : true}) : this.setState({isDataLoaded : false})
        if (this.state.isDataLoaded){
        result.data.length > 4 ? this.setState({size : 4}) : this.setState({size : result.data.length})
        for (var i = 0; i < this.state.size; i++) {
            tmpLink.push(result.data[i].link);
            tmpInfo.push(`Id : ${result.data[i].id} | 
                          Title : ${result.data[i].title} |
                          Description : ${result.data[i].description} | 
                          Type : ${result.data[i].type} | 
                          Views : ${result.data[i].views} | 
                          Vote : ${result.data[i].vote} | 
                          Account Url : ${result.data[i].account_url} | 
                          Account Id : ${result.data[i].account_id} | 
                          Tags : ${result.data[i].tags} | 
                          Width : ${result.data[i].width} | 
                          Height : ${result.data[i].height} | 
                          Name : ${result.data[i].name} | 
                          Link : ${result.data[i].link}`)
          }
        this.setState({
            imgLinks: tmpLink,
            imgInformations: tmpInfo});
          }
        })
      .catch(error => console.log("error : ",error));


    if(this.isDataLoaded)
      this.interval = setInterval(() => this.updateCountDown(), 1000);

  }

  changeBackgroundImage() {
    if (!this.state.pause){
      let newCurrentImg = 0;
      const images = this.state.imgLinks;
      const currentImg = this.state.currentImage
      const noOfImages = images.length;

      if (currentImg !== noOfImages - 1) {
        newCurrentImg = currentImg + 1;
      }
      this.setState({currentImage: newCurrentImg});
      //this.updateCountDown();
    }
  }

  handleImageClick = () => {
    this.setState({pressed : !this.state.pressed});
  }

  handleButtonClick = () => {

    //this.setState({AlbumHash: this.state.userInput})
  }

  handleInputChange = (event) =>{
    //this.setState({userInput : event.target.value})
  }


  render(){    
    const dataLoaded = this.state.isDataLoaded;
    console.log("is data loaded : ", dataLoaded)
    if (!dataLoaded){
      return (
        <div className='App'>
        <input type="text" onChange={ this.handleInputChange } />
        <input
          type="button"
          value="Search"
          onClick={this.handleButtonClick}
        />
        <div>
        Data loading failed, please try again
        </div>
        </div>
      );
    }
    else{
    return (
      <div className='App'>
        <h1 Type an Album Hash/>
        <div><input /></div>
      <div><b>Timer : 00:0{this.state.timer} Seconds</b></div>
      <button><img src={this.state.imgLinks[this.state.currentImage]} onClick={this.handleImageClick}/></button>
      <div>
        <b>{this.state.pressed ? this.state.imgInformations[this.state.currentImage] : 'Click on the Image to show More informations'}</b>
      </div>
      </div>
      );
    }
  }
}

export default App;
