import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import React from 'react';
//import Research from './Research'

class App extends Component {
  constructor(props){
    super(props);

  };

  componentDidMount(){

    var myHeaders = new Headers();
    var formdata = new FormData();
    myHeaders.append("Authorization", "Client-ID 2f598ef310aa471");

    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      //body: formdata,
      redirect: 'follow'
    };

    fetch("https://api.randomuser.me/", requestOptions)
      .then(response => response.text())
      .then(result => console.log("result"))
      .catch(error => console.log(error));
  }

  render(){
    var isLoaded = true;
    if (!isLoaded){
      return <div>Loading...</div>;
    }
    else {
    return (
      <div className='App'>
        <div>
          {isLoaded};ddddd
        </div>
      </div>
      );
    }

  }
}

export default App;
